function findGetParameter(e){var n=null,t=[];return location.search.substr(1).split("&").forEach(function(s){t=s.split("="),t[0]===e&&(n=decodeURIComponent(t[1]))}),n}window.addEventListener("DOMContentLoaded",function(){var e="Ismeretlen válasz. (init)";switch(findGetParameter("redirect_status")){case"succeeded":e="Sikeres rendelés!";break;case"requires_payment_method":e='Még nem adtad meg a vásárlási adataid... ("Hát te meg ki vagy, honnan jöttél?")';break;case"requires_action":e="Még el kell fogadnod a vásárlást...";break;case"processing":e="A rendelésed feldolgozás alatt...";break;case"canceled":e="A rendelésed megszakítottad.";break;default:e="Ismeretlen válasz. ("+findGetParameter("redirect_status")+")";break}document.getElementById("response-title").innerHTML=e})